//******************************************************************************************************************************//
//******************************************************************************************************************************//
// Incluir Bibliotecas: stdio.h, stdlib.h y conio.h (VISTAS EN CLASE Y EN EL CHAMILO EN PRIMER SEMESTRE).-----------------------//
#include <stdio.h> // ( Standard-Input-Output ).--------------------------------------------------------------------------------//
#include <stdlib.h> // ( Standard-Library ).------------------------------------------------------------------------------------//
#include <conio.h> // ( Console-Input-Output ).---------------------------------------------------------------------------------//
// Fin de la inclusion de Bibliotecas.------------------------------------------------------------------------------------------//
// Definiciones de las constantes:----------------------------------------------------------------------------------------------//
#define filas 5 // Se define la cantidad de filas del arreglo. -----------------------------------------------------------------//
#define columnas 5 // Se define la cantidad de columnas del arreglo. -----------------------------------------------------------//
// Fin de las definiciones de las constantes.-----------------------------------------------------------------------------------//
// Definiciones de funciones y arreglos:----------------------------------------------------------------------------------------//
int arreglo[filas][columnas]; // Base de datos vacía donde se ingresaran los datos por parte del administrador/usuario.---------//
void ingresarDatos(); // Se define la funcion para ingresar los datos al arreglo.-----------------------------------------------//
void imprimirArreglo(); // Se define la funcion para imprimir el arreglo.-------------------------------------------------------//
void imprimirEspiral(); //Se define la funcion para imprimir una lista del arreglo en orden de espiral.-------------------------//
// Fin de las definiciones de funciones y arreglos.-----------------------------------------------------------------------------//
//******************************************************************************************************************************//
//******************************************************************************************************************************//
//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++//

//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++//
//******************************************************************************************************************************//
//******************************************************************************************************************************//
void ingresarDatos(){ // Se inicia la definicion de la funcion ingresarDatos.---------------------------------------------------//
	printf("BIENVENIDO: A continuacion ingresaras los valores del arreglo:\n\n"); // Mensaje de Bienvenida al usuario.----------//
	for(int i=0;i<filas;i++){ // Se inicia el primer FOR para recorrer las FILAS.-----------------------------------------------//
		for(int j=0;j<columnas;j++){ // Se inicia el segundo FOR para recorrer las COLUMNAS.------------------------------------//
			printf("\nIngresa un numero para la posicion %ix%i del arreglo: ",i+1,j+1); //Se muestra la ubicacion a ingresar.---//
			scanf("%i",&arreglo[i][j]); // Se ingresa el valor al arreglo en la posicion establecida.---------------------------//
		} // Se cierra el segundo FOR que recorre las COLUMNAS.-----------------------------------------------------------------//
	} // Se cierra el primer FOR que recorre las FILAS.-------------------------------------------------------------------------//
	return;	// Se le da retornar a la funcion tipo void.------------------------------------------------------------------------//
} // Se cierra la llave de la funcion ingresarDatos y con esta sus interacciones locales.---------------------------------------//
//******************************************************************************************************************************//
//******************************************************************************************************************************//
//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++//

//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++//
//******************************************************************************************************************************//
//******************************************************************************************************************************//
void imprimirArreglo(){ // Se inicia la definicion de la funcion imprimirArreglo.-----------------------------------------------//
	printf("\n\n Arreglo original:\n\n"); // Se imprime un mensaje que explica que se va a mostrar el arreglo.------------------//
	for(int i=0;i<filas;i++){ // Se inicia un primer FOR para recorrer las FILAS del arreglo.-----------------------------------//
		for(int j=0;j<columnas;j++){ // Se inicia un segundo FOR para recorrer las COLUMNAS del arreglo.------------------------//
			printf(" %i ",arreglo[i][j]); // Se imprimen los elementos del arreglo según su posicion establecida.---------------//
		} // Se cierra el segundo FOR que recorre las COLUMNAS del arreglo.-----------------------------------------------------//
		printf("\n"); // Se imprime un salto de linea.--------------------------------------------------------------------------//
	} // Se cierra el primer FOR que recorria las FILAS.------------------------------------------------------------------------//
	return; // Se le da retornar a la funcion tipo void.------------------------------------------------------------------------//
} // Se cierra la llave de la funcion imprimirArreglo y con esta sus interaciones locales.--------------------------------------//
//******************************************************************************************************************************//
//******************************************************************************************************************************//
//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++//

//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++//
//******************************************************************************************************************************//
//******************************************************************************************************************************//
void imprimirEspiral(){ // Se inicia la definicion de la funcion imprimirEspiral.-----------------------------------------------//
	printf("\n\n Fila de datos, tomados del arreglo en orden de espiral:\n"); // Se muestra un aviso de impresion de datos.-----//
	for(int j=0;j<columnas;j++){ // Se inicia el primer FOR del primer recorrido en L.------------------------------------------//
		printf(" %i,",arreglo[0][j]); // Se imprimen los elementos de la primera arista de la primer L.-------------------------//
		if(j==4){ // Se pregunta si se llegó al Nodo que une las dos aristas de la primer L.------------------------------------//
			for(int i=1;i<filas;i++){ // Se inicia un segundo FOR para recorrer la segunda Arista de la primer L.---------------//
				printf(" %i,",arreglo[i][4]); // Se imprimen los valores de la segunda Arista de la primer L.-------------------//
			} // Se cierra el segundo FOR que recorre la segunda Arista de la primer L.-----------------------------------------//
		} // Se cierra el IF que preguntaba el Nodo que une las dos Aristas de la primer L.-------------------------------------//
	} // Se finaliza el primer recorrido en L.----------------------------------------------------------------------------------//
	for(int j=3;j>=0;j--){ // Se inicia el segundo recorrido en L.--------------------------------------------------------------//
		printf(" %i,",arreglo[4][j]); // Se imprimen los elementos de la primer Arista del segundo recorrido en L.--------------//
		if(j==0){ // Se inicia la condicional que evalua si se llegó al Nodo que une las dos Aristas de la segunda L.-----------//
			for(int i=3;i>=1;i--){ // Se inicia el recorrido por la segunda Arista del segundo recorrido en L.------------------//
				printf(" %i,",arreglo[i][0]); // Se imprime los elementos de la segunda Arista de la segunda L.-----------------//
			} // Se cierra el FOR que recorre la segunda Arista de la segunda L.------------------------------------------------//
		} // Se cierra la condicional que evalua si se llegó al Nodo que une las dos Aristas de la segunda L.-------------------//
	} // Se cierra el segundo FOR que recorre la segunda L.---------------------------------------------------------------------//
	for(int j=1;j<=3;j++){ // Se inicia un tercer recorrido en forma de L en el arreglo.----------------------------------------//
		printf(" %i,",arreglo[1][j]); // Se imprimen los elementos de la primer arista de la tercer L.--------------------------//
		if(j==3){ // Se evalua si se llegó al Nodo que une las dos Aristas del tercer recorrido en forma de L.------------------//
			for(int i=2;i<=3;i++){ // Se inicia un FOR que recorre la segunda Arista de la tercer L.----------------------------//
				printf(" %i,",arreglo[i][3]); // Se imprimen los elementos de la segunda Arista de la tercer L.-----------------//
			} // Se finaliza el FOR que recorre la segunda Arista de la tercer L.-----------------------------------------------//
		} // Se cierra la condicional que evalua si se llegó al Nodo que une las dos Aristas de la tercer L.--------------------//
	} // Se cierra el FOR que permitia recorrer el arreglo por tercer vez en forma de L.----------------------------------------//
	for(int j=2;j>=1;j--){ // Se inicia un cuarto y ultimo recorrido en L por el arreglo.---------------------------------------//
		printf(" %i,",arreglo[3][j]); // Se imprimen los elementos de la primer Arista del cuarto recorrido en L.---------------//
		if(j==1){ // Se evalua si se llegó al Nodo que une las dos Aristas del cuarto recorrido en L.---------------------------//
			printf(" %i, %i.\n\n",arreglo[2][1],arreglo[2][2]); // Se imprimen los elementos finales del arreglo.---------------//
		} // Se cierra la condicional que evalua si se llegó al Nodo que une las dos Aristas del cuarto recorrido en L.---------//
	} // Se cierra el cuarto recorrido en forma de L por el arreglo.------------------------------------------------------------//
	return; // Se retorna la funcion de tipo void.------------------------------------------------------------------------------//
} // Se finaliza el recorrido e impresion de los elemtos del arreglo en forma de espiral.---------------------------------------//
